function autoSlide(arg) {
	ele = typeof(arg) == "string" ? $(arg) : $(arg.ele);
	var slideEle = $("li", ele);
	var slideBtn = $("#slideBtn");
	if (slideEle.length > 1) {
		slideEle.hide();
		$("li:first", ele).show();
		$("li:first", slideBtn).addClass("cur");
	}
	$("li",slideBtn).click(function(){
		goSlide($(this).index());
        autoSlide.index = $(this).index();
	});

	function goSlide(tango) {
		var current = $("li:visible", ele);
		var next = $("li:eq(" + tango + ")", ele);
		if (current[0] == next[0]) {
			return false
		} else {
			current.stop(false,true).fadeOut(2000);
			next.stop(false,true).fadeIn(2000);
			$("li.cur",slideBtn).removeClass("cur");
			$("li",slideBtn).eq(tango).addClass("cur");
		}
	}
	function autoSlide() {
		if (!autoSlide.index) {
			autoSlide.index = 0
		}
		getIndex = autoSlide.index % slideEle.length;
		autoSlide.index++;
		goSlide(getIndex);
		setTimeout(autoSlide, 8000)
	}
	autoSlide();
}